import "core-js/modules/es.array.push.js";
import { format } from 'date-fns';
export default {
  name: 'SubmitRegistration',
  data() {
    return {
      user: JSON.parse(localStorage.getItem('xm-user') || '{}'),
      doctor: null,
      nowUser: null,
      registrationDate: '',
      registrationTime: '',
      registrationPrice: this.$route.query.price,
      isLoading: true
    };
  },
  created() {
    const doctorId = this.$route.query.doctorId;
    const userId = this.user.id;
    // 格式化日期和时间
    this.registrationDate = format(new Date(this.$route.query.date), 'yyyy-MM-dd');
    this.registrationTime = format(new Date(this.$route.query.time), 'HH:mm:ss');
    if (doctorId) {
      this.fetchDoctorInfo(doctorId);
    } else {
      this.isLoading = false;
    }
    this.fetchUserInfo(userId);
    console.log('当前用户信息:', this.user);
  },
  methods: {
    fetchDoctorInfo(doctorId) {
      this.isLoading = true;
      this.$request.get('/doctor/selectDoctorById', {
        params: {
          doctorId
        }
      }).then(response => {
        if (response.data) {
          this.doctor = response.data;
        } else {
          this.handleError('获取医生详细信息失败', response.data);
        }
      }).catch(error => {
        this.handleError('获取医生详细信息失败', error);
      }).finally(() => {
        this.isLoading = false;
      });
    },
    fetchUserInfo(userId) {
      this.isLoading = true;
      const errorMessage = '获取用户详细信息失败，请稍后重试';
      this.$request.get(`/user/selectById/${userId}`).then(response => {
        if (response.data) {
          this.nowUser = response.data;
        } else {
          this.handleError(errorMessage, response.data);
        }
      }).catch(error => {
        this.handleError(errorMessage, error);
      }).finally(() => {
        this.isLoading = false;
      });
    },
    submitRegistration() {
      console.log('提交挂号信息:', {
        doctorId: this.doctor.id,
        userId: this.nowUser.id,
        registrationDate: this.registrationDate,
        registrationTime: this.registrationTime,
        registrationPrice: this.registrationPrice
      });
      this.$request.post('/registration/insert', {
        doctorId: this.doctor.id,
        userId: this.nowUser.id,
        registrationDate: this.registrationDate,
        registrationTime: this.registrationTime,
        registrationPrice: this.registrationPrice
      }).then(() => {
        this.$message.success('挂号信息提交成功');
        // 跳转到成功页面并传递相关数据
        this.$router.push({
          name: 'RegistrationSuccess',
          params: {
            doctorId: this.doctor.id,
            userId: this.nowUser.id,
            registrationDate: this.registrationDate,
            registrationTime: this.registrationTime,
            registrationPrice: this.registrationPrice
          }
        });
      }).catch(error => {
        this.handleError('提交挂号信息失败', error);
      });
    },
    handleError(message, error) {
      console.error(message, error);
      this.$message.error(`${message}，请稍后重试`);
    }
  }
};
