import "core-js/modules/es.array.push.js";
import { parseTime } from '@/utils';
export default {
  name: 'Doctor',
  data() {
    return {
      doctors: [],
      todaySchedules: [],
      isLoading: true,
      isAppointment: this.$route.params.isAppointment,
      currentHour: new Date().getHours()
    };
  },
  computed: {
    filteredDoctors() {
      return this.doctors.map(doctor => {
        const schedule = this.todaySchedules.find(s => s.doctorId === doctor.id);
        return {
          ...doctor,
          schedule: schedule,
          hasSchedule: !!schedule
        };
      });
    }
  },
  created() {
    this.fetchTodaySchedules().then(() => {
      this.fetchDoctors();
    });
  },
  methods: {
    isAvailable(doctor) {
      if (!doctor.hasSchedule) return false;
      const shiftType = doctor.schedule?.shift_type;
      const isDayTime = this.currentHour >= 8 && this.currentHour < 18;
      if (isDayTime) {
        return true;
      } else {
        return shiftType === 'NIGHT';
      }
    },
    getButtonText(doctor) {
      if (!doctor.hasSchedule) return '今日无排班';
      const shiftType = doctor.schedule?.shift_type;
      const isDayTime = this.currentHour >= 8 && this.currentHour < 18;
      if (isDayTime) {
        return '预约挂号';
      } else {
        if (shiftType === 'DAY') {
          return '当前为夜间时段，无法预约白班医生';
        } else {
          return '预约挂号';
        }
      }
    },
    getShiftTagType(schedule) {
      if (!schedule) return 'info';
      return schedule.shift_type === 'DAY' ? 'success' : 'warning';
    },
    goToRegistration(doctor) {
      const shiftType = doctor.schedule.shift_type;
      if (this.isAppointment) {
        this.$router.push({
          name: 'AppointmentRegister',
          params: {
            doctorId: doctor.id
          },
          query: {
            status: 0,
            shiftType: shiftType
          }
        });
      } else {
        this.$router.push({
          name: 'Registration',
          params: {
            doctorId: doctor.id
          },
          query: {
            status: 0,
            shiftType: shiftType
          }
        });
      }
    },
    async fetchTodaySchedules() {
      const hospitalId = this.$route.params.hospitalId;
      const departmentId = this.$route.params.departmentId;
      const today = parseTime(new Date(), '{y}-{m}-{d}');
      try {
        const response = await this.$request.get('/api/schedule/department/' + departmentId, {
          params: {
            hospitalId: hospitalId,
            startDate: today,
            endDate: today
          }
        });
        this.todaySchedules = response.data || [];
      } catch (error) {
        console.error('获取今日排班失败:', error);
        this.$message.error('获取排班信息失败');
      }
    },
    fetchDoctors() {
      const hospitalId = this.$route.params.hospitalId;
      const departmentId = this.$route.params.departmentId;
      this.$request.get('/doctor/selectDoc', {
        params: {
          hospitalId: hospitalId,
          departmentId: departmentId
        }
      }).then(response => {
        if (response.data) {
          this.doctors = response.data;
        } else {
          console.error('获取医生列表数据格式错误:', response.data);
          this.$message.error('获取医生列表失败，请稍后重试');
        }
      }).catch(error => {
        console.error('获取医生列表失败:', error);
        this.$message.error('获取医生列表失败，请稍后重试');
      }).finally(() => {
        this.isLoading = false;
      });
    },
    getDoctorTitle(status) {
      switch (status) {
        case 1:
          return '医师';
        case 2:
          return '副主治医师';
        case 3:
          return '主治医师';
        case 4:
          return '专家';
        case 5:
          return '急诊';
        default:
          return '未知职称';
      }
    },
    getShiftType(schedule) {
      if (!schedule) return '无排班';
      return schedule.shift_type === 'DAY' ? '白班' : schedule.shift_type === 'NIGHT' ? '夜班' : '无排班';
    }
  }
};
